   $(function() {
        $(".cbp-l-loadMore-defaultText").click(function() {
             var page = $(".cbp-l-loadMore-link").attr('data-page');
             page = parseInt(page);
             $(".cbp-l-loadMore-link").attr('data-page',page+1);
            var href = $(".cbp-l-loadMore-link").attr('data-href');
            $(".cbp-l-loadMore-link").attr('href',href+"?page="+(page+1));

        });
   });
    // init cubeportfolio
    $('#js-grid-juicy-projects').cubeportfolio({
        filters: "#js-filters-juicy-projects",
        loadMore: "#js-loadMore-juicy-projects",
        loadMoreAction: 'click',
        layoutMode: 'grid',
        mediaQueries: [{
            width: 1500,
            cols: 4,
        }, {
            width: 1100,
            cols: 3,
        }, {
            width: 800,
            cols: 3,
        }, {
            width: 480,
            cols: 2,
            options: {
                caption: '',
            }
        }],

        defaultFilter: '*',
        animationType: 'flipOutDelay',
        displayType:'lazyLoading' ,
        gapHorizontal: 10,
        gapVertical: 10,
        gridAdjustment: 'responsive',
        caption: 'overlayBottomAlong',
        displayType: 'sequentially',
        displayTypeSpeed: 100,

        // lightbox
        lightboxDelegate: '.cbp-lightbox',
        lightboxGallery: true,
        lightboxTitleSrc: 'data-title',
        lightboxCounter: '<div class="cbp-popup-lightbox-counter">{{current}} of {{total}}</div>',

    });